import React from 'react';

import SearchBar from './searchbar/searchbar';

import './App.css';

export default class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			url: 'https://www.googleapis.com/books/v1/volumes?',
			key: 'AIzaSyA64mcctC0W3y02ptZwYno6vvuprJ8986M',
			query:'',
			items: []
		};
	}

	searchQuery() {

		let url = `${this.state.url}${this.state.query}&key=${this.state.key}`;

		const options = { method: 'GET' };

		fetch( url, options )

			.then ( res => {

				if ( !res.ok ) {

					throw new Error ( 'Something went wrong, please try again later' );
				}

				return res.json ();
			})

			.then ( data => {

				this.setState({

					items: [...data[0]]

				});

				console.log ( this.state.items );
			})

			.catch ( err => {

				// this.setState({
				//   error: err.message
				// });
				console.log ('error occured');

			});

	}

	queryString(qStr) {

		//console.log (qStr);

		this.setState({

		  query: `q=${qStr}`,

		});

	}

	render () {

		return (

			<div className = 'App'>

				<header className = 'App-header'><h1>Google Book Search</h1></header>

				<SearchBar queryString = {query => this.queryString(query)} searchQuery = {() => this.searchQuery()}/>

				<div className = 'filterbar'>

					<div className = 'print-type-select-container'>

						<label htmlFor = 'print-type-select'>Print Type</label>

						<select className = 'print-type-select'>

						</select>

					</div>

					<div className = 'book-type-select-container'>

						<label htmlFor = 'book-type-select'>Book Type</label>

						<select className = 'book-type-select'>

						</select>

					</div>

				</div>

				<main>



				</main>

			</div>

		);

	}

}
