import React from  'react';

import './searchbar.css';

export default class SearchBar extends React.Component {

  constructor(props) {
     super(props);
     this.state = {
        query:''
     };
  }

  queryString(qStr) {

    console.log (qStr);
    //this.setState({

    //  query: [...this.state.query, qStr],

    //});

  }

  handleSubmit ( e ) {

    alert ( 'sdf' );
    const url = 'https://www.googleapis.com/books/v1/volumes?';
    const query = 'q=';
    const key = 'AIzaSyA64mcctC0W3y02ptZwYno6vvuprJ8986M';
    
    const options = {
      
      method: 'GET',
      //body: JSON.stringify ( query ),
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${ key }`
      }

    };

    fetch(url, options)
      .then(res => {
        if(!res.ok) {
          throw new Error('Something went wrong, please try again later');
        }
        return res.json();
      })
      .then(data => {
        // this.setState({
        //   title: "",
        //   url: "",
        //   description: "",
        //   rating: 1
        // });
        //this.props.handleAdd(bookmark);
        console.log ( data );
      })
      .catch(err => {
        // this.setState({
        //   error: err.message
        // });
        console.log ('error occured');
      });
  }

  render () {

    return (
      
        <div className = 'searchbar'>

            <label htmlFor = 'search-input'>Search</label>

            <input type = 'text' className = 'search-input' onChange={e => this.queryString(e.target.value)} />

            <button onClick = {e => this.props.searchQuery()}>Search</button>

        </div>

    );

  }

}
