import React from 'react';

import SearchBar from './searchbar/searchbar';

import './App.css';

export default class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {query:''};
	}

	searchQuery(query) {
		alert ( this.props.query );
		// this.setState({
		//   bookmarks: [...this.state.bookmarks, bookmark],
		//   showAddForm: false
		// });

	}

	queryString(qStr) {

		console.log (qStr);

		this.setState({

		  query: [qStr],

		});

	}

	render () {

		return (

			<div className = 'App'>

				<header className = 'App-header'><h1>Google Book Search</h1></header>

				<SearchBar queryString = {query => this.queryString(query)} searchQuery = {query => this.searchQuery(query)}/>

				<div className = 'filterbar'>

					<div className = 'print-type-select-container'>

						<label htmlFor = 'print-type-select'>Print Type</label>

						<select className = 'print-type-select'>

						</select>

					</div>

					<div className = 'book-type-select-container'>

						<label htmlFor = 'book-type-select'>Book Type</label>

						<select className = 'book-type-select'>

						</select>

					</div>

				</div>

				<main>



				</main>

			</div>

		);

	}

}
